      let H0l = H0.low;
      const H1h = H1.high;
      let H1l = H1.low;
      const H2h = H2.high;
      let H2l = H2.low;
      const H3h = H3.high;
      let H3l = H3.low;
      const H4h = H4.high;
      let H4l = H4.low;
      const H5h = H5.high;
      let H5l = H5.low;
      const H6h = H6.high;
      let H6l = H6.low;
      const H7h = H7.high;
      let H7l = H7.low;

