      let al = H0l;
      let bh = H1h;
      let bl = H1l;
      let ch = H2h;
      let cl = H2l;
      let dh = H3h;
      let dl = H3l;
      let eh = H4h;
      let el = H4l;
      let fh = H5h;
      let fl = H5l;
      let gh = H6h;
      let gl = H6l;
      let hh = H7h;
      let hl = H7l;

