            const lane = state[laneIndex];
            const TLane = T[laneIndex];
            const Tx1Lane = T[((x + 1) % 5) + 5 * y];
            const Tx2Lane = T[((x + 2) % 5) + 5 * y];

            // Mix rows
            lane.high = TLane.high ^ (~Tx1Lane.high & Tx2Lane.high);
            lane.low = TLane.low ^ (~Tx1Lane.low & Tx2Lane.low);
