          | (((laneMsw << 24) | (laneMsw >>> 8)) & 0xff00ff00);
        laneLsw = (((laneLsw << 8) | (laneLsw >>> 24)) & 0x00ff00ff)
          | (((laneLsw << 24) | (laneLsw >>> 8)) & 0xff00ff00);

