  function f(ctx, x) {
    const a = (x >> 24) & 0xFF;
    const b = (x >> 16) & 0xFF;
    const c = (x >> 8) & 0xFF;
    const d = x & 0xFF;

    let y = ctx.sbox[0][a] + ctx.sbox[1][b];
    y = y ^ ctx.sbox[2][c];
    y = y + ctx.sbox[3][d];

    return y;
  }

  function blowfishEncrypt(ctx, left, right) {
    let Xl = left;
    let Xr = right;
    let temp;

    for(let i = 0; i < N; ++i){
        Xl = Xl ^ ctx.pbox[i];
        Xr = f(ctx, Xl) ^ Xr;

        temp = Xl;
        Xl = Xr;
        Xr = temp;
    }

    temp = Xl;
    Xl = Xr;
    Xr = temp;

    Xr = Xr ^ ctx.pbox[N];
    Xl = Xl ^ ctx.pbox[N + 1];

    return {left: Xl, right: Xr};
  }

  function blowfishDecrypt(ctx, left, right) {
    let Xl = left;
    let Xr = right;
    let temp;

    for(let i = N + 1; i > 1; --i){
        Xl = Xl ^ ctx.pbox[i];
        Xr = f(ctx, Xl) ^ Xr;

        temp = Xl;
        Xl = Xr;
        Xr = temp;
    }

    temp = Xl;
    Xl = Xr;
    Xr = temp;

    Xr = Xr ^ ctx.pbox[1];
    Xl = Xl ^ ctx.pbox[0];

    return {left: Xl, right: Xr};
  }

