  function getCharCodesMapByCode(encoding) {
      const map = new Map();
      let prop;
      switch (encoding) {
          case "StandardEncoding":
              prop = "stdCode";
              break;
          case "WinAnsiEncoding":
              prop = "winCode";
              break;
          case "MacRomanEncoding":
              prop = "macCode";
              break;
          case "PDFDocEncoding":
              prop = "pdfCode";
              break;
          case "Utf-16":
              prop = "utfCode";
              break;
          default:
              console.log(`Unsupported encoding: '${encoding}'`);
              return map;
      }
      for (const [, info] of Object.entries(pdfCharCodesByName)) {
          if (info[prop]) {
              map.set(info[prop], info);
          }
      }
      return map;
  }

